import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { UserData } from '../models/userData';
import { Store } from '@ngxs/store';
import { DeleteUser } from './../state/userState/user.action';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  constructor(private store: Store) {}

  authentication = new BehaviorSubject({});

  getUsersData(): UserData[] {
    let data = JSON.parse(localStorage.getItem('UsersData'));
    return data ?? [];
  }
  getAuthUser(): UserData[] {
    return JSON.parse(localStorage.getItem('AuthUser'));
  }

  checkLogin(UserData: UserData): boolean {
    const jsonData = this.getUsersData();
    let getUser = {};

    getUser = jsonData.find(
      (data) =>
        data.email == UserData.email && data.password == UserData.password
    );
    if (!getUser) {
      return false;
    } else {
      const data = {
        id: getUser['id'],
        name: getUser['name'],
        email: getUser['email'],
        password: getUser['password'],
        isAdmin: getUser['isAdmin'],
      };
      localStorage.setItem('AuthUser', JSON.stringify(data));
      return true;
    }
  }
  uudId(): string {
    return 'xxxx-4xxx-yxxx'.replace(/[xy]/g, function (c) {
      let r = (Math.random() * 6) | 0,
        v = c == 'x' ? r : (r & 0x3) | 0x8;
      return v.toString(6);
    });
  }
  getEmail(email: string): UserData {
    const jsonData = this.getUsersData();
    return jsonData.find((data) => data.email == email);
  }
  register(formData: UserData, isAdmin: boolean): void {
    const getMail = this.getEmail(formData.email);
    if (getMail) {
      alert('Email id already exist !!');
    } else {
      const jsonData = this.getUsersData();
      const data = {
        id: this.uudId(),
        name: formData.name,
        email: formData.email,
        password: formData.password,
        isAdmin: isAdmin,
      };
      jsonData.push(data);
      localStorage.setItem('UsersData', JSON.stringify(jsonData));
    }
  }

  removeAuthUser(): void {
    this.store.dispatch(new DeleteUser());
    localStorage.removeItem('AuthUser');
  }
}

import { Injectable, OnInit } from '@angular/core';
import { Select } from '@ngxs/store';
import { Store } from '@ngxs/store';
import { BsModalService } from 'ngx-bootstrap/modal';
import { Observable } from 'rxjs';
import { DeleteModalComponent } from '../components/main/employees/delete-modal/delete-modal.component';
import { editEmployeeComponent } from '../components/main/employees/edit-employee/edit-employee.component';
import { getEmployee } from '../state/employeeState/employee.action';
import { EmployeeState } from '../state/employeeState/employee.state';
import { Employee } from './../state/employeeState/employee';

@Injectable({
  providedIn: 'root',
})
export class EmployeeService implements OnInit {
  constructor(private store: Store, private modalService: BsModalService) {}
  ngOnInit() {
    this.store.dispatch(new getEmployee());
  }
  @Select(EmployeeState.getEmployee) employee: Observable<Employee[]>;

  getEmployeeById(id:string): Employee {
    let employeesData: Employee[];
    this.employee.subscribe((val) => {
      employeesData = val;
    });
    return employeesData?.find((val) => val.id == id);
  }
  openEditEmployeeModal(id:any): void {
    const employeeData = this.getEmployeeById(id);
    const initialState = {
      id: id,
      employeeData: employeeData,
    };
    this.modalService.show(editEmployeeComponent, { initialState });
  }
  openDeleteEmployeeModal(id:any): void {
    const initialState = {
      id: id,
    };
    this.modalService.show(DeleteModalComponent, { initialState });
  }

  getPageCount(totalRecords: number, pagesize: number): number {
    let totalPage = 0;
    if (totalRecords > 0 && pagesize > 0) {
      const pageCount = totalRecords / pagesize;
      const roundedPageCount = Math.floor(pageCount);
      totalPage =
        roundedPageCount < pageCount ? roundedPageCount + 1 : roundedPageCount;
    }
    return totalPage;
  }
}

import { State, Action, StateContext, Selector } from '@ngxs/store';
import { Employee } from './employee';
import {
  UpdateEmployee,
  getEmployee,
  DeleteEmployee,
  addNewEmployee,
} from './employee.action';
import { Injectable } from '@angular/core';
import { tap } from 'rxjs/operators';
import { StateService } from '../../services/state.service';

export class EmployeeStateModel {
  employees: Employee[];
}

@State<EmployeeStateModel>({
  name: 'employees',
  defaults: {
    employees: [],
  },
})
@Injectable()
export class EmployeeState {
  constructor(private stateService: StateService) {}
  @Selector()
  static getEmployee(state: EmployeeStateModel) {
    return state.employees;
  }

  @Action(getEmployee)
  GetEmployee({ getState, patchState }: StateContext<EmployeeStateModel>) {
    return this.stateService.getEmployeeData().pipe(
      tap((data) => {
        const state = getState();
        patchState({
          employees: data,
        });
      })
    );
  }

  @Action(addNewEmployee)
  AddNewEmployee(
    { getState, patchState }: StateContext<EmployeeStateModel>,
    { data }: addNewEmployee
  ) {
    const state = getState();
    const employeesData = [...state.employees];
    employeesData.push(data);
    patchState({
      employees: employeesData,
    });
  }

  @Action(UpdateEmployee)
  UpdateEmployee(
    { getState, patchState }: StateContext<EmployeeStateModel>,
    { id, data }: UpdateEmployee
  ) {
    const state = getState();
    const employeesData = [...state.employees];
    const dataIndex = employeesData.findIndex((item) => item.id === id);
    employeesData[dataIndex] = data;
    patchState({
      employees: employeesData,
    });
  }
  @Action(DeleteEmployee)
  DeleteEmployee(
    { getState, patchState }: StateContext<EmployeeStateModel>,
    { id }: DeleteEmployee
  ) {
    const state = getState();
    const filteredArray = state.employees.filter((item) => item.id != id);
    patchState({
      employees: filteredArray,
    });
  }
}

