@Injectable({
  providedIn: 'root',
})
export class AuthGuard implements CanActivate {
  constructor(private route: Router, private authService: AuthService) {}
  canActivate(): boolean {
    if (this.authService.getAuthUser()) {
      return true;
    } else {
      this.route.navigate(['/login']);
